<?php    class Encoding	{				public static function toUtf($string)		{			return iconv('windows-1251', 'UTF-8', $string);		}				public static function toCp($string)		{			return iconv('UTF-8', 'windows-1251', $string);		}				public static function arrayToUtf($array)		{			foreach ($array as $key => $value)			{				if(is_string($value) && !is_numeric($value))				{					$array[$key] = toUtf($value);				}				else if(is_array($value))				{					$array[$key] = arrayToUtf($value);				}			}			return $array;		}				public static function arrayToCp($array)		{			foreach ($array as $key => $value)			{				if(is_string($value))				{					$array[$key] = toCp($value);				}				else if(is_array($value))				{					$array[$key] = arrayToCp($value);				}			}			return $array;		}	}?>