<?php    class File	{				/**		    Сжимает и записывает в файл						$dir - где находится			$filename - название файла			$data - содержимое		*/		public static function writeToCompressedFile($dir, $filename, $data)		{			@mkdir($dir, 0755, true);						$stream = @fopen($dir.$filename, 'w+');			@fwrite($stream, gzdeflate($data));			@fclose($stream);						return $dir.$filename;		}				/**		    Читает и разархивирует файл						$dir - где находится			$filename - название файла		*/		public static function readCompresedFile($dir, $filename)		{			return gzinflate(@file_get_contents($dir.$filename));		}				/**		    Записывает в файл						$dir - где находится			$filename - название файла			$data - содержимое		*/		public static function writeToUncompressedFile($dir, $filename, $data)		{			@mkdir($dir, 0755, true);						$stream = @fopen($dir.$filename, 'w+');			@fwrite($stream, $data);			@fclose($stream);						return $dir.$filename;		}				/**		    Читает файл						$dir - где находится			$filename - название файла		*/		public static function readUncompressedFile($dir, $filename)		{			return @file_get_contents($dir.$filename);		}				/**		    Удаляет файл						$dir - где находится			$filename - название файла		*/		public static function deleteFile($dir, $filename)		{		    $file = $dir.$filename;//иначе ошибка синтаксиса		    unset($file);			return true;		}				/**		    Очищает содержимое папки		*/		public static function clearDirectory($dir) 		{			rmdir($dir);			mkdir($dir, 0755);			return true;  		} 	}?>