<?php$searches = array(    '</passage><passage>',    '<title>',    '</title>',    '<hlword>',    '</hlword>',    '<passages>',    '<passage>',    '</passage>',    '</passages>',);$replacements = array(    '#br#',    '',    '',    '#b#',    '#/b#',    '',    '',    '',    '',);$replacements2 = array(    '<br>',    '',    '',    '<b>',    '</b>',    '',    '',    '',    '',);$title = str_replace($replacements, $replacements2, htmlspecialchars(str_replace($searches, $replacements, $d->doc->title->asXML())));$passages = array();foreach($d->doc->passages as $passage)    $passages[] = htmlspecialchars(trim(str_replace($searches, $replacements, $passage->asXML())));$passages = str_replace($replacements, $replacements2, implode('#br#', $passages));$url = htmlspecialchars($d->doc->url);?><div class="result_row"><a href="<?php echo $url; ?>" class="mainlink" target="_blank">    <?php echo $title; ?></a><div>    <?php echo $passages; ?><br></div><a href="<?php echo $url; ?>" class="final_link" target="_blank"><?php echo substr($url, 0, 100); if(strlen($url) > 100) echo '...'; ?></a></div>